import React, { useState } from "react";
import { Box, Container, Typography, TextField, Button, List, ListItem, ListItemText, IconButton } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import "./todo.css"

const Todo = () => {
  const [input, setInput] = useState("");
  const [items, setItems] = useState([]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleAddItem = () => {
    if (input.trim() !== "") {
      setItems([...items, input.trim()]);
      setInput("");
    }
  };

  const handleDeleteItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  return (
    <Container maxWidth="md" sx={{ textAlign: "center", marginTop: 4, border: "1px solid red", display: "flex", flexDirection: "column" }}>
      <Typography variant="h4" component="div" gutterBottom>
        To-Do List
      </Typography>
      <Box sx={{border:"2px solid red",display:"f"}}>
        <TextField
          label="Add items"
          variant="outlined"
          value={input}
          onChange={handleInputChange}
          sx={{ marginBottom: 2 }}
        />

        <Button variant="contained" onClick={handleAddItem}>
          Add
        </Button>

      </Box>

      <List sx={{ marginTop: 2, width: "100%", maxWidth: 360, bgcolor: "background.paper" }}>
        {items.map((item, index) => (
          <ListItem key={index} secondaryAction={
            <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteItem(index)}>
              <DeleteIcon />
            </IconButton>
          }>
            <ListItemText primary={item} />
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default Todo;
