import React, { useState } from "react";
import logo from "./logo512.png";

function Todo() {

    const [getInput, setGetInput] = useState("");
    const[items, setItems] = useState([]);


    const getEvent = e =>{
        setGetInput(e.target.value)
    }
    const addItem = e =>{
        e.preventDefault();
        setItems([...items, getInput]);
        setGetInput("");
    }
    const deleteItem = (e, id) =>{
        e.preventDefault();
       return setItems(items.filter(currItem => currItem.id !== id))


        
    }

  return (
    <>
    {/* {JSON.stringify(items)} */}
      <div className="container">
        <figure>
          <img src={logo} alt="logo" height={100} />
          <figcaption>Add your list</figcaption>
        </figure>
        <div className="addItem">
          <input type="text" value={getInput} placeholder="add items" onChange={getEvent}/>
        </div>
        <div className="list">
            {items.map((val,index)=>{
                return (
                    <p key={index} id={index}>{val} <button onClick={deleteItem}>delete</button> </p>
            )})}
        </div>
        <button onClick={addItem}>CHECK LIST</button>
      </div>
    </>
  );
}

export default Todo;
